package day03;

public class 제어문_반복문_for {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
		for(int i=1;i<=3;i++) {
			System.out.println("안녕하세요");
		}
		
		for(int j=2;j<=9;j++) {
			for(int i=1;i<=9;i++) {
			System.out.println(j+" * " +i+ " = " + 2*i);
		}
		}
		
		
	}

}
